import { Component } from '@angular/core';
import { FormGroup, FormControl,Validators} from '@angular/forms';
//import { HttpClient } from '@angular/common/http';
//import { ValidatorService } from 'src/app/services/validator.service';

@Component({
  selector: 'app-compo1',
  templateUrl: './compo1.component.html',
  styleUrls: ['./compo1.component.css']
})

export class compo1Component   {

  //constructor (private http:HttpClient) {}

  LoginForm =new FormGroup({


    name:new FormControl('',[
      Validators.required,
      Validators.minLength(2),
      Validators.pattern('[a-zA-Zç]{6,}$')
    ]),

    password:new FormControl('',[
      Validators.required,
      Validators.pattern('[a-zA-Z0-9]{8,}$')
    ])

  });

  //constructor(private ValidatorService : ValidatorService){
//    this.name = this.ValidatorService.getData();
//
//  }
}
<div class="container"> 
    <h3>Login</h3>
    <!--<form [formGroup]="miform" (ngSubmit)="onSubmit()">-->
	<div class="w-full max-w-xs">
		<form [formGroup]="LoginForm" class="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">

			<!-- Nombre -->
			<p> Nombre de usuario: <br>
				<input type="text" formControlName="name">

				<span  *ngIf="this.LoginForm.get('name')?.errors?.['required'] && 
						this.LoginForm.get('name')?.touched">
					El nombre no puede estar vacío
				</span>
				<span   *ngIf="this.LoginForm.get('name')?.errors?.['minlength']&& 
						this.LoginForm.get('name')?.touched">
					El nombre no puede tener menos de 2 letras
				</span>

				<span   *ngIf="this.LoginForm.get('name')?.errors?.['pattern'] && 
						this.LoginForm.get('name')?.touched">
					Solo puede contener letras, minimo 6
				</span>

			</p>

			<p> Contrasenya: <br>
				<input type="password" formControlName="password">

				<span  *ngIf="this.LoginForm.get('password')?.errors?.['required'] && 
						this.LoginForm.get('password')?.touched">
					La contrasenya no puede estar vacía
				</span>

				<span   *ngIf="this.LoginForm.get('password')?.errors?.['pattern'] && 
						this.LoginForm.get('password')?.touched">
					La contrasenya no tiene el formato correcto
				</span>
			</p>


			<button type="submit" [disabled]="LoginForm.invalid">Login</button>
		</form>
	</div>
</div>
